import React from "react";

class FormButton extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            openDialog:false,
        }
        this.handleClick = this.handleClick.bind(this)
    }

    handleClick(){
        this.setState(prevState=>({
            openDialog: !prevState.openDialog
        }))
    }

    render(){
        return(
        <div className = "columnLeft">
            <button onClick={this.handleClick}>{this.state.openDialog ? FormExpansion :"Open Dialog"}</button>
        </div>
        )
    }


}
class FormExpansion extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            formOpened: true,
        }
    }
    openDialogForm(){ 
            return(
            <div className = "columnLeft">
                <form id = "Information">
                    <input 
                    type = "url"
                    name = "picLink"
                    placeholder="Enter Picture link here"
                    />
                    <input
                    type = "text"
                    name = "picName"
                    placeholder="Enter Picture Name Here"
                    />
                    <button>Accept</button>
                    <button onClick={()=>{this.setState({FormRendered: false})}}>Cancel</button>
                </form>
            </div>
        )
    }

}

export default FormButton;
