import React from 'react'

class Button extends React.Component{
    constructor(props){
        super(props);
        this.state={
            openForm: false
        }
        this.handleClick = this.handleClick.bind(this)
    }

    handleClick(){
        this.setState(prevState =>({
            openForm : !prevState.openForm
        })) 
    }

    render(){
        return(
        <div className="columnLeft">
            <button onClick={this.handleClick}>Open Dialog</button>
            {this.state.openForm ? <Form/> : ""}
        </div> 
        )
    }
}

class Form extends React.Component{
    constructor(props){
        super(props);
        this.state={
            link: "",
            caption: ""         
        }
        this.handleLink = this.handleLink.bind(this)
        this.handleName = this.handleName.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleCancel = this.handleCancel.bind(this)
    }



    handleLink(event){
        this.setState({
            link: event.target.value
        })
    }
    handleName(event){
        this.setState({
            caption: event.target.value
        })
    }
    handleSubmit(){
        return(
            <div><Card link = {this.state.link} caption = {this.state.caption}/></div>
        )
    }
    handleCancel(){
        this.setState({
            link: "",
            caption: "",
        }) 
    }

    render(){
        return (
            <div className = "columnLeft">
            <form 
            onSubmit={this.handleSubmit}
            id = "Information"
            >
                <fieldset>
                <input 
                type = "url"
                onChange={this.handleLink}
                value = {this.state.link}
                name = "picLink"
                placeholder="Enter Picture link Here"
                />
                <input
                type = "text"
                onChange={this.handleName}
                value = {this.state.caption}
                name = "picName"
                placeholder="Enter Picture Name Here"
                />
                <button type="submit">Accept</button>
                <button onClick={this.handleCancel}>Cancel</button>
                </fieldset>
            </form>

            </div>



            )
    }   
}

