import React from 'react'
import Form from './Form';

//Button Component held here. Parent of Form

class Button extends React.Component{
    constructor(props){
        super(props);
        this.state={
            openForm: false,
            cancel: false
        }
        this.handleClick = this.handleClick.bind(this)
        this.handleCancel = this.handleCancel.bind(this)
    }

    handleClick(){
        this.setState(prevState =>({
            openForm : !prevState.openForm,
            cancel: !prevState.cancel
        })) 
    }

    handleCancel(){
        this.setState(prevState=>({
            openForm: !prevState.openForm,
            cancel: !prevState.cancel
        }))
    }

    render(){
        return(
        <div>
            <button className="ButtonForm" onClick={this.handleClick}>Open Dialog</button>
            {this.state.openForm ? <Form className="columnLeft" cancel={this.state.cancel}/> : null}
            {this.state.cancel ? <button className="ButtonCancel"onClick={this.handleCancel}>Cancel</button> : null}
        </div> 
        )
    }
}

export default Button
