import React from "react";

class FormButton extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            openDialog:false,
        }
        this.render = this.render.bind(this)
    }
    render(){
        return(
        <div>
            <button onClick={() => {this.setState({FormRendered: true})}}>Open Dialog</button>
        </div>
        )
    }


}
class FormExpansion extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }
    openDialog = () =>{
        return(
            <div>
                <form id = "Information">
                    <input 
                    type = "url"
                    name = "picLink"
                    placeholder="Enter Picture link here"
                    />
                    <input
                    type = "text"
                    name = "picName"
                    placeholder="Enter Picture Name Here"
                    />
                    <button>Accept</button>
                    <button onClick={()=>{this.setState({FormRendered: false})}}>Cancel</button>
                </form>
            </div>
        )
    }
}

export default FormButton;
