import React from 'react'

let cardArray = [];
let id = 0;

class App extends React.Component{

    constructor(props){
        super(props);
        this.state={
            cardArrayData: cardArray
        }
        this.cardArrayState = this.cardArrayState.bind(this)
    }

    cardArrayState(){
        this.setState({
            cardArrayData: cardArray
        })
    }

    render(){
        return(
            <div>
            <Button cardState={this.cardArrayState}/>
            </div>

        );
    }
}

class Button extends React.Component{
    constructor(props){
        super(props);
        this.state={
            openForm: false,
            cancel: false
        }
        this.handleClick = this.handleClick.bind(this)
        this.handleCancel = this.handleCancel.bind(this)
    }

    handleClick(){
        this.setState(prevState =>({
            openForm : !prevState.openForm,
            cancel: !prevState.cancel
        })) 
    }

    handleCancel(){
        this.setState(prevState=>({
            openForm: !prevState.openForm,
            cancel: !prevState.cancel
        }))
    }

    render(){
        return(
        <div>
            <button className="ButtonForm" onClick={this.handleClick}>Open Dialog</button>
            {this.state.openForm ? <Form className = "columnLeft" cancel={this.state.cancel}/> : null}
            {this.state.cancel ? <button className="ButtonCancel"onClick={this.handleCancel}>Cancel</button> : null}
        </div> 
        )
    }
}
class Form extends React.Component{
    constructor(props){
        super(props);
        this.state={
            link: "",
            caption: "",
            error: false,   
            displayCard: false   
        }
        this.handleLink = this.handleLink.bind(this)
        this.handleName = this.handleName.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleCancel = this.handleCancel.bind(this)

    }

    handleLink(event){
        this.setState({
            link: event.target.value
        })
    }
    handleName(event){
        this.setState({
            caption: event.target.value
        })
    }
    handleSubmit(event){
        event.preventDefault()
        if(this.state.link==="" ){
            this.setState({
                error: true,
                link: null,
                displayCard: false
            })
        }
        else if(this.state.caption===""){
            this.setState({
                error:true,
                caption: null,
                displayCard: false
            })
        }
        else{
            this.setState({
                displayCard:true,
                error:false
            })
        }
        console.log(this.state.link)
        console.log(this.state.caption)
    }

    handleCancel(){
        if(this.props.cancel === true){
        this.setState({
            link: null,
            caption: null,

        })}
    }

    render(){
        return (
            <div>
            <form className = "columnLeft"
            onSubmit={this.handleSubmit}
            id = "Information"
            >
                <fieldset>
                <input 
                type = "url"
                onChange={this.handleLink}
                value = {this.state.link}
                name = "link"
                placeholder="Enter Picture link Here"
                />
                <input
                type = "text"
                onChange={this.handleName}
                value = {this.state.caption}
                name = "caption"
                placeholder="Enter Picture Name Here"
                />
                <button className="Button"type="submit">Accept</button>
                </fieldset>
            </form>
            {this.state.error ? <p className='Error'>"Enter a link AND a caption"</p>: null}
            {this.state.displayCard ? <Card className='columnRight' link={this.state.link} caption={this.state.caption}/> : null }
            </div>

        )
    }   
}

class Card extends React.Component{
    constructor(props){
        super(props);
        this.state={
            cardArray:[
                {
                    Link: "",
                    Caption:""
                }

            ],
        }

    }

    addCardData(){

    }

    render(){
        return(
            <div className='Card'>
                <img src = {this.props.link}  width = '150px' height ='150px' alt="this is not a valid link"></img>
                <p>{this.props.caption}</p>
                <button>x</button>
            </div>

        )

    }
}

export default App
