import React from 'react'
import Card from './createCard'

class Form extends React.Component{
    constructor(props){
        super(props);
        this.state={
            link: "",
            caption: "",
            error: false,   
            displayCard: false    
        }
        this.handleLink = this.handleLink.bind(this)
        this.handleName = this.handleName.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleCancel = this.handleCancel.bind(this)

    }

    handleLink(event){
        this.setState({
            link: event.target.value
        })
    }
    handleName(event){
        this.setState({
            caption: event.target.value
        })
    }
    handleSubmit(event){
        event.preventDefault()
        if(this.state.link==="" ){
            this.setState({
                displayCard: true,
                error: true,
                link: null
            })
        }
        else if(this.state.caption===""){
            this.setState({
                error:true,
                caption: null,
            })
        }
        else{
            this.setState({
                error:false
            })
        }
        console.log(this.state.link)
        console.log(this.state.caption)
    }

    handleCancel(){
        this.setState({
            link: null,
            caption: null,
        }) 
    }

    render(){
        return (
            <div className = "columnLeft">
            <form 
            onSubmit={this.handleSubmit}
            id = "Information"
            >
                <fieldset>
                <input 
                type = "url"
                onChange={this.handleLink}
                value = {this.state.link}
                name = "link"
                placeholder="Enter Picture link Here"
                />
                <input
                type = "text"
                onChange={this.handleName}
                value = {this.state.caption}
                name = "caption"
                placeholder="Enter Picture Name Here"
                />
                <button type="submit">Accept</button>
                <button onClick={this.handleCancel}>Cancel</button>
                </fieldset>
            </form>
            {this.state.error ? <p className='Error'>"Enter a link AND a caption"</p>: null}
            {this.state.displayCard ? <Card link={this.state.link} caption={this.state.caption}/> : null }
            </div>

        )
    }   
}

export default Form
