import React from "react";

class FormButton extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            openDialog:false,
        }
        this.handleClick = this.handleClick.bind(this)
    }

    handleClick(){
        this.setState(prevState=>({
            openDialog: !prevState.openDialog
        }))
    }

    render(){
        return(
        <div className = "columnLeft">
            <button onClick={this.handleClick}>{this.state.openDialog ? FormExpansion.setState.opened = true :"Open Dialog"}</button>
        </div>
        )
    }   
}

class FormExpansion extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            opened: false,
            link: "",
            setName:"",
        }
        this.handleLink = this.handleLink.bind(this)
        this.handleName = this.handleName.bind(this)
    }

    handleLink(event){
        this.setState({
            link: event.target.value
        })
    }

    handleName(event){
        this.setState({
            setName: event.target.value
        })
    }

    render(){
        if(this.state.opened = true){
            return(
            <div className = "columnLeft">
                <form id = "Information">
                    <fieldset>
                    <input 
                    type = "url"
                    onLoad = {this.handleLink}
                    name = "picLink"
                    placeholder="Enter Picture link here"
                    />
                    <input
                    type = "text"
                    onLoad={this.handleName}
                    name = "picName"
                    placeholder="Enter Picture Name Here"
                    />
                    <button type="submit">Accept</button>
                    <button onClick={this.setState = false}>Cancel</button>
                    </fieldset>
                </form>
            </div>
            )
        }
    }
}

export default FormButton;
