import React from "react";

class FormButton extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            openDialog: true,
            openForm: false,
            link: "",
            setName:"",
            imageAdd: false
        }
        this.handleClickButtonRender = this.handleClickButtonRender.bind(this)
        this.handleLink = this.handleLink.bind(this)
        this.handleName = this.handleName.bind(this)
        this.handleImageSubmission = this.handleImageSubmission.bind(this)
    }

    handleClickButtonRender(){
        this.setState(prevState=>({
            openDialog: !prevState.openDialog,
            openForm: !prevState.openForm
        }))
    }

    handleLink(event){
        this.setState({
            link: event.target.value
        })
    }

    handleName(event){
        this.setState({
            setName: event.target.value
        })
    }
    handleImageSubmission(){
        return(
            <div className="columnRight">
                <img src = {this.state.link} width="300px" height="300px" alt=""></img>
                <p>{this.state.name}</p>
            </div>
        )
    }


    renderButton(){
        if(this.state.openDialog === true){
            return(
            <div className = "columnLeft">
            <button onClick={this.handleClickButtonRender}>Open Dialog</button>
            </div>
            )
        }

    }
    renderForm(){
        if(this.state.openForm === true){
            return (
            <div className = "columnLeft">
            <form 
            id = "Information"
            onSubmit = {this.handleImageSubmission}
            >
                <fieldset>
                <input 
                type = "url"
                onInputCapture={this.handleLink}
                name = "picLink"
                placeholder="Enter Picture link Here"
                />
                <input
                type = "text"
                onInputCapture={this.handleName}
                name = "picName"
                placeholder="Enter Picture Name Here"
                />
                <button type="submit">Accept</button>
                <button onClick={this.handleClickButtonRender}>Cancel</button>
                </fieldset>
            </form>
            </div>
            )
        }
        else{
            return
        }
    }


    render(){
        return(
            <div>
            {this.renderButton()}
            {this.renderForm()}
            </div>

        );
    }   
}




export default FormButton;
