import React from "react";

class FormButton extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            openDialog: true,
            openForm: false,
            link: "",
            setName:"",
        }
        this.handleClick = this.handleClick.bind(this)
        this.handleLink = this.handleLink.bind(this)
        this.handleName = this.handleName.bind(this)
    }

    handleClickButton(){
        this.setState(prevState=>({
            openDialog: !prevState.openDialog,
            openForm: !prevState.openForm
        }))
    }

    handleLink(event){
        this.setState({
            link: event.target.value
        })
    }

    handleName(event){
        this.setState({
            setName: event.target.value
        })
    }
    renderButton(){
        if(this.state.openDialog === true){
            return(
            <div className = "columnLeft">
            <button onClick={this.handleClickButton}>"Open Dialog"</button>
            </div>
            )
        }

    }
    renderForm(){
        if(this.state.openForm === true){
            return (
            <div className = "columnLeft">
            <form id = "Information">
                <fieldset>
                <input 
                type = "url"
                onInput = {this.handleLink}
                name = "picLink"
                placeholder="Enter Picture link here"
                />
                <input
                type = "text"
                onInput ={this.handleName}
                name = "picName"
                placeholder="Enter Picture Name Here"
                />
                <button type="submit">Accept</button>
                <button onClick={this.setState.openForm = false}>Cancel</button>
                </fieldset>
            </form>
            </div>
            )
        }
        else{
            return
        }
    }


    render(){
        return(
            <div>
            {this.renderButton()}
            {this.renderForm()}
            </div>

        );
    }   
}




export default FormButton;
