import React from "react";


class App extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            openDialog: true,
            openForm: false,
            link: "",
            setName:"",
        };
        this.handleClickButtonRender = this.handleClickButtonRender.bind(this)
        this.handleLink = this.handleLink.bind(this)
        this.handleName = this.handleName.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleImageSubmission = this.handleImageSubmission.bind(this)
    }

    handleClickButtonRender(){
        this.setState(prevState=>({
            openDialog: !prevState.openDialog,
            openForm: !prevState.openForm,
        }))

    }

    handleLink(event){
        this.setState({
            link: event.target.value
        })
    }

    handleName(event){
        this.setState({
            setName: event.target.value
        })
    }

    handleSubmit(event){
        if(this.state.link!=="" || this.state.setName!==""){
            return(
                <div className="Error">
                <p>Please Enter a Valid Response</p>
                </div>
            )
        }
        else{
            event.preventDefault();
            this.handleImageSubmission();
            this.handleClickButtonRender();
        }
    }

    handleImageSubmission(){

     }



    renderButton(){
        if(this.state.openDialog === true){
            return(
            <div className = "columnLeft">
            <button onClick={this.handleClickButtonRender}>Open Dialog</button>
            </div>
            )
        }

    }
    renderForm(){
        if(this.state.openForm === true){
            return (
            <div className = "columnLeft">
            <form 
            id = "Information"
            >
                <fieldset>
                <input 
                type = "url"
                onChange={this.handleLink}
                value = {this.state.link}
                name = "picLink"
                placeholder="Enter Picture link Here"
                />
                <input
                type = "text"
                onChange={this.handleName}
                value = {this.state.setName}
                name = "picName"
                placeholder="Enter Picture Name Here"
                />
                <button type="submit" onClick={this.handleSubmit}>Accept</button>
                <button onClick={this.handleClickButtonRender}>Cancel</button>
                </fieldset>
            </form>
            </div>
            )
        }
        else{
            return
        }
    }

    render(){
        return(
            <div>
            {this.renderButton()}
            {this.renderForm()}
            </div>

        );
    }   
}

